package d2

val input = """



"""

val input2 = """ULL
RRDDD
LURDL
UUUUD"""

val DIGITS2 = """
  1
 234
56789
 ABC
  D
""".trimMargin().split("\n")

val DIGITS = listOf(listOf(1, 2, 3), listOf(4, 5, 6), listOf(7, 8, 9))
val DIRS = mapOf('U' to (0 to -1), 'R' to (1 to 0), 'D' to (0 to 1), 'L' to (-1 to 0))

var x = 0
var y = 2
var code = ""

fun main(args: Array<String>) {
    input.split("\n").forEach {
        for (c in it) {
            val (dx, dy) = DIRS[c]!!
            val nextDigit = DIGITS2.elementAtOrNull(y + dy)?.elementAtOrNull(x + dx)
            if (nextDigit == null || nextDigit == ' ') continue

            x += dx
            y += dy
        }
        code += DIGITS2[y][x]
    }
    println(code)
}